INSERT INTO CYCategories(PK_CYCategory_ID, Indicator, Name, Category, Description, Date_Created) VALUES(1177,'Ds.1','AutorunsPersistence','Destruction','This object attempts to interact with common methods of persistence (startup scripts, etc). Malware commonly uses these tactics to attain persistence', CURRENT_TIMESTAMP);
INSERT INTO CYCategories(PK_CYCategory_ID, Indicator, Name, Category, Description, Date_Created) VALUES(1178,'Ds.2','FileDirDeleteImports','Destruction','This PE imports functions that can be used to delete Files or Directories. Malware uses this to break systems and cover its tracks', CURRENT_TIMESTAMP);
INSERT INTO CYCategories(PK_CYCategory_ID, Indicator, Name, Category, Description, Date_Created) VALUES(1179,'Ds.3','PossibleLocker','Destruction','This object demonstrates evidence of a desire to lock out common tools by policy. Malware will do this to retain persistence and make detection and cleanup more difficult', CURRENT_TIMESTAMP);
INSERT INTO CYCategories(PK_CYCategory_ID, Indicator, Name, Category, Description, Date_Created) VALUES(1180,'Ds.4','RegistryManipulation','Destruction','This object imports functions that are used to manipulate the windows registry. Malware does this to attain persistence, avoid detection, and for many other reasons', CURRENT_TIMESTAMP);
INSERT INTO CYCategories(PK_CYCategory_ID, Indicator, Name, Category, Description, Date_Created) VALUES(1181,'Ds.5','SeBackupPrivilege','Destruction','This PE might attempt to read files to which it has not been granted access. SeBackup privilege allows access to files without honoring access controls. It is frequently used by programs that handle backups, and is frequently limited to administrative users, but can be used maliciously to get access to specific elements that might otherwise be difficult', CURRENT_TIMESTAMP);
INSERT INTO CYCategories(PK_CYCategory_ID, Indicator, Name, Category, Description, Date_Created) VALUES(1182,'Ds.6','SeDebugPrivilege','Destruction','This PE might attempt to tamper with system processes. SeDebug Privilege is used to access processes other than your own and is frequently limited to administrative users. It is often paired with reading and writing to other processes', CURRENT_TIMESTAMP);
INSERT INTO CYCategories(PK_CYCategory_ID, Indicator, Name, Category, Description, Date_Created) VALUES(1183,'Ds.7','SeRestorePrivilege','Destruction','This PE might attempt to change or delete files to which it has not been granted access. The pair to SeBackup, SeRestore privilege allows writing without consideration of access control', CURRENT_TIMESTAMP);
INSERT INTO CYCategories(PK_CYCategory_ID, Indicator, Name, Category, Description, Date_Created) VALUES(1184,'Ds.8','ServiceControlImports','Destruction','This object imports functions that can control windows services on the current system. Malware uses this to either launch itself into the background via installing as a service, or to disable other services that may have a protective function', CURRENT_TIMESTAMP);
INSERT INTO CYCategories(PK_CYCategory_ID, Indicator, Name, Category, Description, Date_Created) VALUES(1185,'Ds.9','SpawnProcessImports','Destruction','This PE imports functions that can be used to spawn another process. Malware uses this to launch subsequent phases of an infection, typically downloaded from the Internet', CURRENT_TIMESTAMP);
INSERT INTO CYCategories(PK_CYCategory_ID, Indicator, Name, Category, Description, Date_Created) VALUES(1186,'Ds.10','TerminateProcessImports','Destruction','This object imports functions that can be used to stop a running process. Malware uses this to attempt to remove protection systems, or to cause damage to a running system', CURRENT_TIMESTAMP);
